import React from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const BarChart = () => {
  const data = {
    labels: ["2020", "2021"], 
    datasets: [
      {
        label: "", 
        data: [65, 59, 80, 81, 56], 
        backgroundColor: "#27648f", 
        borderColor: "#28648f", 
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "Monthly Sales Data",
      },
    },
  };

  return (
    <div style={{ width: "250px", height: "fit-content" }}>
      <Bar data={data} options={options} width={150} height={100} />
    </div>
  );
};

export default BarChart;
